mapper.insert:
SELECT LAST_INSERT_ID()
INSERT INTO admin_user (
) VALUES ( #{deleteFlag}, #{address}, #{mobile}, #{email}, #{sex}, #{district}, #{city}, #{province}, #{loginCount}, #{lastLoginIp}, #{lastLoginTime}, #{avator}, #{nickName}, #{salt}, #{password}, #{userName}, #{status}, #{userType}, #{updateUserId}, #{updateTime}, #{addUserId}, #{addTime}, #{uuid}, #{id} )

mapper.select:
select  from admin_user where 1=1

and delete_flag like CONCAT('%',#{deleteFlag},'%')


and address like CONCAT('%',#{address},'%')


and mobile like CONCAT('%',#{mobile},'%')


and email like CONCAT('%',#{email},'%')


and sex like CONCAT('%',#{sex},'%')


and district=#{district}


and city=#{city}


and province=#{province}


and login_count=#{loginCount}


and last_login_ip like CONCAT('%',#{lastLoginIp},'%')


and last_login_time=#{lastLoginTime}


and avator like CONCAT('%',#{avator},'%')


and nick_name like CONCAT('%',#{nickName},'%')


and salt like CONCAT('%',#{salt},'%')


and password like CONCAT('%',#{password},'%')


and user_name like CONCAT('%',#{userName},'%')


and status like CONCAT('%',#{status},'%')


and user_type like CONCAT('%',#{userType},'%')


and update_user_id=#{updateUserId}


and update_time=#{updateTime}


and add_user_id=#{addUserId}


and add_time=#{addTime}


and uuid like CONCAT('%',#{uuid},'%')


and id=#{id}


    order by  ${sortStr}

